function populateCalculatorList(calculators) {
    const calculatorList = document.getElementById('calculator-list');
    calculatorList.innerHTML = '';
    calculators.forEach(calc => {
        const card = document.createElement('div');
        card.className = 'calculator-card';
        card.innerHTML = `
            <h2>${calc.name}</h2>
            <a href="${calc.url}">Go to Calculator</a>
        `;
        calculatorList.appendChild(card);
    });
}

function searchCalculators() {
    const input = document.getElementById('searchInput');
    const filter = input.value.toUpperCase();
    // Use the global 'calculators' array defined at the top of the script
    const filteredCalculators = calculators.filter(calc => calc.name.toUpperCase().includes(filter));
    populateCalculatorList(filteredCalculators);
}

function populateRelatedCalculators(calculators) {
    const relatedList = document.getElementById('related-calculators');
    relatedList.innerHTML = '';
    const shuffled = calculators.sort(() => 0.5 - Math.random());
    const selected = shuffled.slice(0, 10);

    selected.forEach(calc => {
        const listItem = document.createElement('li');
        listItem.innerHTML = `<a href="${calc.url}">${calc.name} Calculator</a>`;
        relatedList.appendChild(listItem);
    });
}


// Specific calculation functions
function calculateGravelVolume() {
    const length = parseFloat(document.getElementById('length').value);
    const width = parseFloat(document.getElementById('width').value);
    const depth = parseFloat(document.getElementById('depth').value);

    if (isNaN(length) || isNaN(width) || isNaN(depth) || length < 0 || width < 0 || depth < 0) {
        document.getElementById('result').innerText = 'Please enter valid positive numbers for all fields.';
        return;
    }

    const volumeCubicFeet = length * width * (depth / 12);
    const volumeCubicYards = volumeCubicFeet / 27;

    document.getElementById('result').innerText = `You need ${volumeCubicYards.toFixed(2)} cubic yards of gravel.`;
}

function calculateLawnMowingCost() {
    const area = parseFloat(document.getElementById('area').value);
    const rate = parseFloat(document.getElementById('rate').value);

    if (isNaN(area) || isNaN(rate) || area < 0 || rate < 0) {
        document.getElementById('result').innerText = 'Please enter valid positive numbers for area and rate.';
        return;
    }

    const totalCost = area * rate;

    document.getElementById('result').innerText = `Estimated mowing cost: $${totalCost.toFixed(2)}.`;
}

function calculateFenceLength() {
    const length = parseFloat(document.getElementById('length').value);
    const width = parseFloat(document.getElementById('width').value);

    if (isNaN(length) || isNaN(width) || length < 0 || width < 0) {
        document.getElementById('result').innerText = 'Please enter valid positive numbers for length and width.';
        return;
    }

    const perimeter = 2 * (length + width);

    document.getElementById('result').innerText = `You need ${perimeter.toFixed(2)} feet of fencing.`;
}

function calculateGardenSoilVolume() {
    const length = parseFloat(document.getElementById('length').value);
    const width = parseFloat(document.getElementById('width').value);
    const depth = parseFloat(document.getElementById('depth').value);

    if (isNaN(length) || isNaN(width) || isNaN(depth) || length < 0 || width < 0 || depth < 0) {
        document.getElementById('result').innerText = 'Please enter valid positive numbers for all fields.';
        return;
    }

    const volumeCubicFeet = length * width * (depth / 12);

    document.getElementById('result').innerText = `You need ${volumeCubicFeet.toFixed(2)} cubic feet of garden soil.`;
}

function calculateWaterUsage() {
    const area = parseFloat(document.getElementById('area').value);
    const gallonsPerSqFt = parseFloat(document.getElementById('gallons_per_sqft').value);
    const frequency = parseFloat(document.getElementById('frequency').value);

    if (isNaN(area) || isNaN(gallonsPerSqFt) || isNaN(frequency) || area < 0 || gallonsPerSqFt < 0 || frequency < 0) {
        document.getElementById('result').innerText = 'Please enter valid positive numbers for all fields.';
        return;
    }

    const totalGallonsPerWeek = area * gallonsPerSqFt * frequency;

    document.getElementById('result').innerText = `Estimated water usage: ${totalGallonsPerWeek.toFixed(2)} gallons per week.`;
}

// --- Calculator Logic --- //
// Generic function for all calculators.
// Specific logic for each calculator should be added here or in separate functions.
function calculate(calculatorName) {
    const resultElement = document.getElementById('result');
    if (!resultElement) return;

    switch (calculatorName) {
        case 'Gravel Installation Cost':
            calculateGravelVolume();
            break;
        case 'Lawn Mowing Cost':
            calculateLawnMowingCost();
            break;
        case 'Fence Installation Cost':
            calculateFenceLength();
            break;
        case 'Garden Soil Cost':
            calculateGardenSoilVolume();
            break;
        case 'Sprinkler System Installation Cost': // Using this as a proxy for Water Usage
            calculateWaterUsage();
            break;
        default:
            resultElement.innerText = `Calculation logic for ${calculatorName} goes here.`;
            // Provide guidance for adding custom logic
            resultElement.innerHTML += '<br><small>To add specific logic for this calculator, edit the `calculate` function in `script.js`.</small>';
            break;
    }
}
